mapper.select:
select summary_id, member_id, source_name, source_url, title, published_at, summary_text, detail_summary, analysis_json, keywords_json, created_at from news_summary where member_id = #{memberId}
AND DATE(published_at) = CURRENT_DATE
AND published_at >= CURRENT_DATE - INTERVAL '7 days'
AND published_at >= CURRENT_DATE - INTERVAL '1 month'
AND ( published_at < #{lastPublishedAt} OR (published_at = #{lastPublishedAt} AND summary_id < #{lastSummaryId}) )
AND (
title LIKE CONCAT('%', #{keyword}, '%') OR CAST(keywords_json AS TEXT) LIKE CONCAT('%', #{keyword}, '%')
) order by published_at DESC, created_at DESC limit #{limit}
