mapper.insert:
INSERT INTO portfolio_standard(
    standard_name, standard_description, prompt_template, weight_percentage
) VALUES(
    #{standardName}, #{standardDescription}, #{promptTemplate}, 
    COALESCE(#{weightPercentage}, 20)
)

mapper.update:
UPDATE portfolio_standard SET 
    standard_name = #{standardName},
    standard_description = #{standardDescription},
    prompt_template = #{promptTemplate},
    weight_percentage = #{weightPercentage}
WHERE standard_id = #{standardId}
